aa0 = BasicPartition("|0,1|2,3|4,5|")
aa1 = BasicPartition("|0,2|1,4|3,5|")
bl = aa1.getBlocks()
def blockSums(part):
    bb = part.getBlocks()
    ans = []
    for b in bb:
        ans.append(sum(b))
    return ans

def blockSumsList(l):
    ans = []
    for b in l:
        ans.append(sum(b))
    return ans

def centerBlocks(part,k):
    bb = part.getBlocks()
    ans = []
    for b in bb:
        ans.append([x-k for x in b])
    return ans


blockSums(aa0)             


